import 'es6-promise/auto'
import Vue from 'vue'
import Element from 'element-ui'
import locale from 'element-ui/lib/locale/lang/en'
import Fullscreen from 'vue-fullscreen'
import VueLazyload from 'vue-lazyload'
import vClickOutside from 'v-click-outside'
import VirtualList from 'vue-virtual-scroll-list'
import VuePerfectScrollbar from './components/vue-ps'
import './assets/scss/global.scss'
import 'flex.box'
import 'animate.css'
import Affix from './directives/affix'
import App from './App.vue'
import router from './router/' 
import store from './store' 
import "@mdi/font/scss/materialdesignicons.scss";
import OtpInput from "@bachdgvn/vue-otp-input";
import Notification from "./plugins/Notification";

Vue.config.productionTip = false
Vue.use(Fullscreen)
Vue.use(Element, {locale})
Vue.use(vClickOutside)
Vue.use(Notification);
Vue.use(VueLazyload, {
	preLoad: 1.3,
	error: 'data:image/svg+xml;base64,',
	loading: 'data:image/svg+xml;base64,',
	attempt: 1
})
Vue.directive('affix', Affix)
Vue.component('vue-scroll', VuePerfectScrollbar)
Vue.component('vue-virtual-list', VirtualList)
Vue.component("v-otp-input", OtpInput);
new Vue({
	router,
	store,
	render: h => h(App)
}).$mount('#app')
